import React, { useEffect, useState } from 'react';
import { matriculaService } from '../Services/MatriculaService';

const MatriculaList: React.FC = () => {
  const [matriculas, setMatriculas] = useState<any[]>([]);

  // Carregar as matrículas quando o componente for montado
  useEffect(() => {
    const fetchMatriculas = async () => {
      const matriculasData = await matriculaService.listarMatriculas();
      setMatriculas(matriculasData);
    };
    fetchMatriculas();
  }, []);

  // Remover matrícula de um aluno de um curso
  const handleRemoverMatricula = async (alunoId: number, cursoId: number) => {
    await matriculaService.removerMatricula(alunoId, cursoId);
    setMatriculas(matriculas.filter(
      (matricula) => matricula.AlunoId !== alunoId || matricula.CursoId !== cursoId
    ));
  };

  return (
    <div>
      <h3>Matrículas</h3>
      <ul>
        {matriculas.map((matricula) => (
          <li key={`${matricula.AlunoId}-${matricula.CursoId}`}>
            {matricula.Aluno.Nome} está matriculado no curso {matricula.Curso.Nome}
            <button onClick={() => handleRemoverMatricula(matricula.AlunoId, matricula.CursoId)}>
              Remover
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MatriculaList;
