import React, { useState } from 'react';
import AlunoList from './components/AlunoList.tsx';
import CursoList from './components/CursoList.js';
import MatriculaList from "./Components/MatriculaList.js"

const App: React.FC = () => {
  // Controle de quais seções estão visíveis
  const [section, setSection] = useState<'alunos' | 'cursos' | 'matriculas'>('alunos');

  return (
    <div className="App">
      <h1>Gestão de Alunos, Cursos e Matrículas</h1>

      {/* Menu de navegação */}
      <div>
        <button onClick={() => setSection('alunos')}>Gerenciar Alunos</button>
        <button onClick={() => setSection('cursos')}>Gerenciar Cursos</button>
        <button onClick={() => setSection('matriculas')}>Gerenciar Matrículas</button>
      </div>

      {/* Renderizar seções baseadas no botão selecionado */}
      {section === 'alunos' && <AlunoList />}
      {section === 'cursos' && <CursoList />}
      {section === 'matriculas' && <MatriculaList />}
    </div>
  );
}

export default App;
